import { useState } from 'react';
import FacebookLogin from 'react-facebook-login';
import axios from 'axios';
const Test = () => {

    const [accessToken, setAccessToken] = useState("");
    const [permaUserToken, setUserPermaToken] = useState("")
    const componentClicked = (data) => {
        console.log("data -> ", data)
    }

    const responseFacebook = (res) => 
    {

        console.log("tar -> ",res.accessToken)
        setAccessToken(res.accessToken);

        axios.get(`https://graph.facebook.com/v16.0/oauth/access_token?grant_type=fb_exchange_token&client_id=444890921111161&client_secret=adb45080a68a158f5e29104bdf425549&fb_exchange_token=${res.accessToken}`).then(
            (res)=>{
                    console.log("long lived access token ",res.data["access_token"])
                    setUserPermaToken(res.data["access_token"])


                    axios.get(`https://graph.facebook.com/v16.0/me?access_token=${res.data["access_token"]}`).then(
                        (res2)=>{
                            console.log("current user id", res2.data["id"])


                            // getting the page access token for this user
                        }
                    ).catch((err2)=>
                    {
                        console.log(err2)
                    })


            }
        ).catch((err)=>{console.log(err)})


    }
    return (
        <div>
            <h3>Facebook Login </h3>
            <FacebookLogin
                appId="444890921111161"
                autoLoad={true}
                fields="name,email,picture"
                onClick={componentClicked}
                callback={responseFacebook}
            />,
        </div>
    )
}

export default Test
