import { useState } from "react"
import { GoLocation } from 'react-icons/go';
import { BiArrowBack } from 'react-icons/bi';
import "../SignIn.css"
import "../SignUp.css"

const SignUp = () => {

    const [location, setLocation] = useState(true);
    const [email, setEmail] = useState(false);
    const [ph, setPh] = useState(false);
    const [bioData, setBio] = useState(false);

    const [firstName, setFirstName] = useState("");
    const [LastName, setLastName] = useState("");
    const [password, setPass] = useState("");
    const [confirmPass, setConfirmPass] = useState("");

    return (
        <div className="signUp formContainer">

            <h1>
                Sign Up and Enjoy
            </h1>

            {location ?
                <form className="form locations" action="">
                    <div className="address">
                        <GoLocation size={25} />
                        <select name="" id="" placeholder='Your Postal Code' >
                            <option>
                                Your Postal Code
                            </option>
                        </select>
                    </div>
                    <div className="buttonWrapper">
                        <button className="btn" onClick={
                            () => {
                                setEmail(true)
                                setLocation(false)
                            }
                        }>
                            Next
                        </button>
                    </div>



                </form>

                : null

            }
            {!location && email ?

                <form action="" className="form email">
                    <h4>
                        Email
                    </h4>
                    <div className="input" id="emailInput">


                        <input type="email"
                            placeholder='Enter the email '
                            style={{ "fontSize": "1rem", "padding": "0rem 0.5rem" }} />
                    </div>
                    <div className="buttonWrapper rflex">

                        <BiArrowBack className="icons hoverRound" size={25}
                        style ={{"color":"white"}}
                            onClick={
                                () => {
                                    setEmail(false)
                                    setLocation(true)
                                }
                            }

                        />

                        <button className="btn" onClick={
                            () => {
                                setEmail(false)
                                setLocation(false)
                                setPh(true)
                            }
                        }>
                            Next
                        </button>
                    </div>
                </form>
                : null

            }
            {!location && !email && ph ?
                <form action="" className="form email number">
                    <h4>
                        Phone Number
                    </h4>
                    <div className="input" id="emailInput">

                        <select name="" id="" style={{ "padding": "0.4rem 0.6rem", "border": "1px solid", "fontSize": "1rem" }}>
                            <option value="">
                                Country
                            </option>
                        </select>
                        <input type="text"
                            placeholder='Enter the number '
                            style={{ "fontSize": "1rem", "padding": "0rem 0.5rem" }} />
                    </div>
                    <div className="buttonWrapper rflex">

                        <BiArrowBack className="icons hoverRound" size={25}
                        style ={{"color":"white"}}
                            onClick={
                                () => {
                                    setEmail(true)
                                    setLocation(false)
                                    setPh(false)
                                    setBio(false)
                                }
                            }

                        />

                        <button className="btn" onClick={
                            () => {
                                setEmail(false)
                                setLocation(false)
                                setPh(false)
                                setBio(true)
                            }
                        }>
                            Next
                        </button>
                    </div>
                </form>

                : null

            }
            {!location && !email && !ph && bioData ?


                <form action="" className="form bioData" >

                    <h4>
                        First Name
                    </h4>
                    <div className="input">


                        <input type="email"
                            placeholder='your legal first name '

                            style={{ "fontSize": "1rem", "padding": "0.4rem 0.5rem" }}

                            value={firstName}
                            onChange={(e) => { setFirstName(e.target.value) }}
                        />
                    </div>

                    <h4>
                        Last Name
                    </h4>
                    <div className="input">


                        <input type="email"
                            placeholder='your legal last name '
                            value={LastName}
                            onChange={
                                (e) => { setLastName(e.target.value) }
                            }
                            style={{ "fontSize": "1rem", "padding": "0.4rem 0.5rem" }} />
                    </div>

                    <h4>
                        Password
                    </h4>
                    <div className="input pass">
                        <input placeholder='Enter the password '

                            type="password" style={{ "fontSize": "1rem", "padding": "0.4rem 0.5rem" }} value={password} onChange={(e) => {

                                setPass(e.target.value)

                            }}
                        />
                    </div>
                    <h4>
                        Confirm Password
                    </h4>
                    <div className="input pass">
                        <input placeholder='Confirm the password '

                            type="password" style={{ "fontSize": "1rem", "padding": "0.4rem 0.5rem" }} value={confirmPass} onChange={(e) => {

                                setConfirmPass(e.target.value)

                            }}
                        />
                    </div>


                    <div className="buttonWrapper rflex">

                    <BiArrowBack className="icons hoverRound" size={25}
                    style ={{"color":"white"}}
                            onClick={
                                () => {
                                    setEmail(false)
                                    setLocation(false)
                                    setPh(true)
                                    setBio(false)
                                }
                            }

                        />


                        {(confirmPass) === (password) !== "" ?
                            <button className="btn width">
                                Sign Up
                            </button> :
                            <button className="btn width" style={{"pointerEvents":"none"}}>
                                Sign Up
                            </button>
                        }
                    </div>


                </form>

                : null

            }



        </div>
    )
}

export default SignUp
