import "../App.css"
import { BsFacebook } from 'react-icons/bs';
import { useEffect, useState } from "react";
import axios from "axios"
const Main = () => {

    const [img, setImg] = useState(false);
    const [vid, setVid] = useState(false);

    const [file, setFile] = useState([])


    const uploadToServer = () => {

        if (img) {
            console.log("upload request for img")

            axios.post(`https://graph.facebook.com/106647510736091/photos?url=https://i.natgeofe.com/n/548467d8-c5f1-4551-9f58-6817a8d2c45e/NationalGeographic_2572187_square.jpg&access_token=EAAGUoD1RDnkBAJ94cZBNmFVKj3JN0zmnOpztT7hhXB6P8kolfZCNAtoGLgfZBwctZB3Gsr8VNU0WZCj2kuD41Pk41irNBgiYdr3rybkC6heVmm3C5msYXAGKHfFoNlaISO7ftv5P5OMuRJEv1wRfrKtsgMVZB6rEtMeZBqSKeZA0KOqEJmEao05WRIqhVp6fihoZD`).then(
                (res)=>{
                    console.log(res.data)
                }
            ).catch((err)=>{console.log(err)})


            console.log(file[0])
        }
        else if (vid) {
            console.log("upload request for video")
        }


    }

    const handleChange = (e)=>{
        setFile([...file, e.target.files[0]]);

    }
    return (
        <div className="landing">
            <div className="uploadBtns center">
                <h1 className="white">
                    Choose to upload
                </h1>
                {!img && !vid ?
                    <div className="options  ">

                        <button className="optionBtn btn"
                            onClick={() => { setImg(true) }}
                        >
                            Upload Picture
                        </button>
                        <button className="optionBtn btn"
                            onClick={() => { setVid(true) }}
                        >
                            Upload Video
                        </button>
                    </div> :
                    null}


                {img ?
                    <div className="upload">
                        <input className="btn" type="file" accept="image/png, image/jpeg" 
                            onChange=
                            {
                                (e) => { handleChange(e) }
                            } />
                    </div> : null
                }
                {vid ?
                    <div className="upload">
                        <input className="btn" type="file" accept="image/png, image/jpeg" 
                            onChange=
                            {
                                (e) => { handleChange(e) }
                            }
                        />
                    </div> : null

                }

                {
                    file[0] ?
                        <button className="btn round" onClick={() => {
                            uploadToServer()
                        }}>
                            Upload
                        </button> : null
                }


            </div>

            <div className="graphics">
                <BsFacebook style={{ "color": "white" }} size={350} />
            </div>
        </div>

    )
}

export default Main
