import "../App.css"
import { BsFacebook } from 'react-icons/bs';
import { useEffect, useState } from "react";
import axios from "axios"
const Main = () => {

    const [img, setImg] = useState(false);
    const [vid, setVid] = useState(false);

    const [file, setFile] = useState('')


    const uploadToServer = () => {

        const formData =  new FormData();
        formData.append(file)
        if (img) {

            console.log("file ",file)

            axios.post("https://graph.facebook.com/100089772745438/photos?",{
                url:formData,
                access_token:"EAAGUoD1RDnkBALq8ZBO6iuPDZBAacM7nCDK84YM0LctjBBNLKWO4RvQH1wxf0rAZBhjaCNMRFDXAqcD8WYZB6nrUCF2vmadJXJHvGZA4pegBMEEybuAQN08pwZAMZC7t4K9LVMBk1vIw7Mq1EjIGtdZC4gVYwRkBxBGKYKZCrX9gdmAkGWdBAOlPU3p0NZCSxLCZAcUnPDWOrAjkAZDZD"}
                ).then( 
                (res)=>{
                    console.log(res.data)
                }
            ).catch((err)=>{console.log(err)})


            console.log(file)
        }
        else if (vid) {
            console.log("upload request for video")
        }


    }

    const handleChange = (e)=>
    {

        // const imageFile = e.target.files[0];

        // if (imageFile) {
        //     const reader = new FileReader();

        //     // Convert the image file to a string
        //     reader.readAsDataURL(imageFile);

        //     // FileReader will emit the load event when the data URL is ready
        //     // Access the string using result property inside the callback function
        //     reader.addEventListener('load', () => {
        //         // Get the data URL string
        //         setFile(reader.result);
        //     });
        // }


        setFile(e.target.files[0]);

    }
    return (
        <div className="landing">
            <div className="uploadBtns center">
                <h1 className="white">
                    Choose to upload
                </h1>
                {!img && !vid ?
                    <div className="options  ">

                        <button className="optionBtn btn"
                            onClick={() => { setImg(true) }}
                        >
                            Upload Picture
                        </button>
                        <button className="optionBtn btn"
                            onClick={() => { setVid(true) }}
                        >
                            Upload Video
                        </button>
                    </div> :
                    null}


                {img ?
                    <div className="upload">
                        <input className="btn" type="file" accept="image/png, image/jpeg" 
                            onChange=
                            {
                                (e) => { handleChange(e) }
                            } />
                    </div> : null
                }
                {vid ?
                    <div className="upload">
                        <input className="btn" type="file" accept="image/png, image/jpeg" 
                            onChange=
                            {
                                (e) => { handleChange(e) }
                            }
                        />
                    </div> : null

                }

                {
                    file!=="" ?
                        <button className="btn round" onClick={() => {
                            uploadToServer()
                        }}>
                            Upload
                        </button> : null
                }


            </div>

            <div className="graphics">
                <BsFacebook style={{ "color": "white" }} size={350} />
            </div>
        </div>

    )
}

export default Main
