import "../App.css"
import { BsFacebook } from 'react-icons/bs';
import { useEffect, useState } from "react";
import axios from "axios"
const Main = () => {

    const [img, setImg] = useState(false);
    const [vid, setVid] = useState(false);

    const [file, setFile] = useState('')


    const uploadToServer = () => {

        if (img) {


            console.log("upload request for img")

            let caption = "test upload local image from React";
            const fileReader = new FileReader();
            fileReader.onloadend = async () => {
              if (fileReader.result != null) {
                const photoData = new Blob([fileReader.result], { type: "image/png" });
                const formData = new FormData();
                formData.append("source", photoData);
                formData.append("caption", caption);
                httpFacebookGraphClient(facebookToken)
                  .post("/" + ldlTestFacebookGroupId + "/photos", formData, {
                    headers: {
                      "Content-Type": "multipart/form-data",
                    },
                  })
                  .then((res) => {
                    console.log(res.data);
                  })
                  .catch((err) => {
                    console.log(err);
                  });
              }
            };

            axios.post("https://graph.facebook.com/100089772745438/photos?",{
                url:file[0],
                access_token:"EAAGUoD1RDnkBALq8ZBO6iuPDZBAacM7nCDK84YM0LctjBBNLKWO4RvQH1wxf0rAZBhjaCNMRFDXAqcD8WYZB6nrUCF2vmadJXJHvGZA4pegBMEEybuAQN08pwZAMZC7t4K9LVMBk1vIw7Mq1EjIGtdZC4gVYwRkBxBGKYKZCrX9gdmAkGWdBAOlPU3p0NZCSxLCZAcUnPDWOrAjkAZDZD"}
                ).then( 
                (res)=>{
                    console.log(res.data)
                }
            ).catch((err)=>{console.log(err)})


            fileReader.readAsArrayBuffer(fileToUpload);
            console.log(file)
        }
        else if (vid) {
            console.log("upload request for video")
        }


    }

    const handleChange = (e)=>{
        setFile(URL.createObjectURL(e.target.files[0]));

    }
    return (
        <div className="landing">
            <div className="uploadBtns center">
                <h1 className="white">
                    Choose to upload
                </h1>
                {!img && !vid ?
                    <div className="options  ">

                        <button className="optionBtn btn"
                            onClick={() => { setImg(true) }}
                        >
                            Upload Picture
                        </button>
                        <button className="optionBtn btn"
                            onClick={() => { setVid(true) }}
                        >
                            Upload Video
                        </button>
                    </div> :
                    null}


                {img ?
                    <div className="upload">
                        <input className="btn" type="file" accept="image/png, image/jpeg" 
                            onChange=
                            {
                                (e) => { handleChange(e) }
                            } />
                    </div> : null
                }
                {vid ?
                    <div className="upload">
                        <input className="btn" type="file" accept="image/png, image/jpeg" 
                            onChange=
                            {
                                (e) => { handleChange(e) }
                            }
                        />
                    </div> : null

                }

                {
                    file[0] ?
                        <button className="btn round" onClick={() => {
                            uploadToServer()
                        }}>
                            Upload
                        </button> : null
                }


            </div>

            <div className="graphics">
                <BsFacebook style={{ "color": "white" }} size={350} />
            </div>
        </div>

    )
}

export default Main
