import "../App.css"
import { BsFacebook } from 'react-icons/bs';
import { useEffect, useState } from "react";

const Main = () => {

    const [img, setImg] = useState(false);
    const [vid, setVid] = useState(false);

    const [file, setFile] = useState("")

    // useEffect(() => {
    //     setImg(false);
    //     setVid(false)
    // })
    return (
        <div className="landing">
            <div className="uploadBtns center">
                <h1 className="white">
                    Choose to upload
                </h1>
                {!img&&!vid?
                <div className="options  ">

                    <button className="optionBtn btn"
                       onClick={()=>{setImg(true)}}
                    >
                        Upload Picture
                    </button>
                    <button className="optionBtn btn"
                    onClick={ ()=>{setVid(true)}}
                    >
                        Upload Video
                    </button>
                </div>:
                null}


                {img ?
                    <div className="upload">
                        <input className="btn" type="file" accept="image/png, image/jpeg" value={file} onChange = {(e)=>{setFile(e.target.value)}} />
                    </div> : null
                }
                {vid ?
                   <div className="upload">
                   <input className="btn" type="file" accept="image/png, image/jpeg" value={file} onChange = {(e)=>{setFile(e.target.value)}} />
               </div> : null

                }

                {
                ready?
                <button className="btn round">
                    Upload
                </button>:null
}


            </div>

            <div className="graphics">
                <BsFacebook style={{ "color": "white" }} size={350} />
            </div>
        </div>

    )
}

export default Main
