import "../App.css"
import SessionManager from "./SessionManager";
import { BsFacebook } from 'react-icons/bs';
import { useEffect, useState } from "react";
import axios from "axios"
const Main = () => {

    const [img, setImg] = useState(false);
    const [vid, setVid] = useState(false);
    const [file, setFile] = useState('');



    const uploadToServer = () => {

        const formData = new FormData();
        // if(vid)
        // {
        //     // var endpoint = "https://graph-video.facebook.com/v16.0/100089772745438/videos"
        //     var endpoint = "http://localhost:4000/api/user/proxy"
        //     formData.append("video_file_chunk", file)

        // }
        // else if(img)
        // {
            // endpoint = "https://graph.facebook.com/100089772745438/photos?"
            formData.append("source", file)

        // }

        if (img || vid) {


            console.log("form data", formData)

            axios.post("https://graph.facebook.com/100089772745438/photos?",

                formData,
                {
                    headers:
                    {
                        "Content-Type": "multipart/form-data",
                        "Authorization": "Bearer EAAGUoD1RDnkBALq8ZBO6iuPDZBAacM7nCDK84YM0LctjBBNLKWO4RvQH1wxf0rAZBhjaCNMRFDXAqcD8WYZB6nrUCF2vmadJXJHvGZA4pegBMEEybuAQN08pwZAMZC7t4K9LVMBk1vIw7Mq1EjIGtdZC4gVYwRkBxBGKYKZCrX9gdmAkGWdBAOlPU3p0NZCSxLCZAcUnPDWOrAjkAZDZD"
                    }
                }

            ).then(
                (res) => {
                    console.log(res)
                    if (res.data["id"]) {
                        alert("Upload Successfully")
                    }
                }
            ).catch((err) => {
                alert("Error communicating to server")
                console.log(err)
            })


            console.log(file)
        }
        else if (vid) {
            console.log("upload request for video")
        }


    }

    const handleChange = (e) => {


        setFile(e.target.files[0]);

    }


    return (

        <div className="landing">
            <SessionManager homeLink={"/signin"} endpoint={"http://localhost:4000/api/user/session"} style={{ "display": "none" }} />
            <div className="uploadBtns center">
                <h1 className="white">
                    Choose to upload
                </h1>
                {!img && !vid ?
                    <div className="options  ">

                        <button className="optionBtn btn"
                            onClick={() => { setImg(true) }}
                        >
                            Upload Picture
                        </button>
                        <button className="optionBtn btn"
                            onClick={() => { setVid(true) }}
                        >
                            Upload Video
                        </button>
                    </div> :
                    null}


                {img ?
                    <div className="upload">
                        <input className="btn" type="file" accept="image/png, image/jpeg"
                            onChange=
                            {
                                (e) => { handleChange(e) }
                            } />
                    </div> : null
                }
                {vid ?
                    <div className="upload">
                        <input className="btn" type="file" accept="video/mp4,video/x-m4v,video/*"
                            onChange=
                            {
                                (e) => { handleChange(e) }
                            }
                        />
                    </div> : null

                }

                {
                    file !== "" ?
                        <button className="btn round" onClick={() => {
                            uploadToServer()
                        }}>
                            Upload
                        </button> : null
                }


            </div>

            <div className="graphics">
                <BsFacebook style={{ "color": "white" }} size={350} />
            </div>
        </div>

    )
}

export default Main
