import "../App.css"
import SessionManager from "./SessionManager";
import { BsFacebook } from 'react-icons/bs';
import { useEffect, useState } from "react";
import axios from "axios"
const Main = () => {

    const [img, setImg] = useState(false);
    const [vid, setVid] = useState(false);
    const [file, setFile] = useState('');



    const uploadToServer = async () => {

        const formData = new FormData();
        if (img) {

            formData.append("source", file)

            axios.post("https://graph.facebook.com/100089772745438/photos?",

                formData,
                {
                    headers:
                    {
                        "Content-Type": "multipart/form-data",
                        "Authorization": "Bearer EAAGUoD1RDnkBALq8ZBO6iuPDZBAacM7nCDK84YM0LctjBBNLKWO4RvQH1wxf0rAZBhjaCNMRFDXAqcD8WYZB6nrUCF2vmadJXJHvGZA4pegBMEEybuAQN08pwZAMZC7t4K9LVMBk1vIw7Mq1EjIGtdZC4gVYwRkBxBGKYKZCrX9gdmAkGWdBAOlPU3p0NZCSxLCZAcUnPDWOrAjkAZDZD"
                    }
                }

            ).then(
                (res) => {
                    if (res.data["id"]) {
                        alert("Upload Successfully")
                    }
                }
            ).catch((err) => {
                alert("Error communicating to server")
                console.log(err)
            })


            console.log(file)
        }
        else if (vid) {

            // const accessToken = "EAAGUoD1RDnkBALq8ZBO6iuPDZBAacM7nCDK84YM0LctjBBNLKWO4RvQH1wxf0rAZBhjaCNMRFDXAqcD8WYZB6nrUCF2vmadJXJHvGZA4pegBMEEybuAQN08pwZAMZC7t4K9LVMBk1vIw7Mq1EjIGtdZC4gVYwRkBxBGKYKZCrX9gdmAkGWdBAOlPU3p0NZCSxLCZAcUnPDWOrAjkAZDZD"

            // const formData = new FormData();
            // formData.append('file_url', "https://www.youtube.com/watch?v=3HvzgDzrG0c");

            // console.log("-< ",formData)
            // formData.append('published', 'true');
            // formData.append('title', 'My Video');
            // formData.append('description', 'This is my video');
            // try {
            //     const res = await fetch(
            //       `https://graph.facebook.com/v16.0/100089772745438/videos?access_token=${accessToken}`,
            //       {
            //         method: 'POST',
            //         body: {'file_url': "https://www.youtube.com/watch?v=3HvzgDzrG0c"},
            //         // headers: {
            //         //     "Content-Type": "multipart/form-data"}
            //       }
            //     );

            //     if (res.status === 200) {
            //       alert('Video published successfully');
            //     } else {
            //         console.log(res)
            //       alert('Video publishing failed');
            //     }
            //   } catch (error) {
            //     alert('Error while publishing video');
            //   }

            // const formData = new FormData();
            formData.append("file", file);

            axios.post("https://graph-video.facebook.com/v16.0/100089772745438/videos",
                formData,
                {
                    headers:
                    {
                        "Content-Type": "multipart/form-data",
                        "Authorization": "Bearer EAAGUoD1RDnkBALq8ZBO6iuPDZBAacM7nCDK84YM0LctjBBNLKWO4RvQH1wxf0rAZBhjaCNMRFDXAqcD8WYZB6nrUCF2vmadJXJHvGZA4pegBMEEybuAQN08pwZAMZC7t4K9LVMBk1vIw7Mq1EjIGtdZC4gVYwRkBxBGKYKZCrX9gdmAkGWdBAOlPU3p0NZCSxLCZAcUnPDWOrAjkAZDZD"
                    }
                }

            ).then(
                (res) => {
                    if (res.data["id"]) {
                        alert("Upload Successfully")
                    }
                }
            ).catch((err) => {
                alert("Error communicating to server")
                console.log(err)
            })        
            console.log(file)

        }



    }

    const handleChange = (e) => {


        setFile(e.target.files[0]);

    }


    return (

        <div className="landing">
            <div className="uploadBtns center">
                <h1 className="white">
                    Choose to upload
                </h1>
                {!img && !vid ?
                    <div className="options  ">

                        <button className="optionBtn btn"
                            onClick={() => { setImg(true) }}
                        >
                            Upload Picture
                        </button>
                        <button className="optionBtn btn"
                            onClick={() => { setVid(true) }}
                        >
                            Upload Video
                        </button>
                    </div> :
                    null}


                {img ?
                    <div className="upload">
                        <input className="btn" type="file" accept="image/png, image/jpeg"
                            onChange=
                            {
                                (e) => { handleChange(e) }
                            } />
                    </div> : null
                }
                {vid ?
                    <div className="upload">
                        <input className="btn" type="file" accept="video/mp4,video/x-m4v,video/*"
                            onChange=
                            {
                                (e) => { handleChange(e) }
                            }
                        />
                    </div> : null

                }

                {
                    file !== "" ?
                        <button className="btn round" onClick={() => {
                            uploadToServer()
                        }}>
                            Upload
                        </button> : null
                }


            </div>

            <div className="graphics">
                <BsFacebook style={{ "color": "white" }} size={350} />
            </div>
        </div>

    )
}

export default Main
